import React, { Component } from "react";
import axios from "axios";
import {
  TextField,
  Paper,
  Card,
  CardContent,
  Typography,
  CardActions,
  Button,
} from "@material-ui/core";
import classes from "./Search.css";

class Search extends Component {
  state = {
    searchingTerm: "",
    noResult: true,
    books: [],
  };

  setSearchingTerm = (value) => {
    this.setState({ searchingTerm: value });
    console.log("term: ", this.state.searchingTerm);
  };

  searchForBook = () => {
    let term = this.state.searchingTerm;

    //str.replace("Microsoft", "W3Schools");
    term.replace(" ", "+");
    let url = "http://openlibrary.org/search.json?q=" + term + "/";
    axios
      .get(url)
      .then((response) => {
        console.log("search->response: ", response);
        if (response.data.docs.length == 0) {
          console.log("array empty");
          this.setState({ noResult: true });
        }
        let array = response.data.docs.slice(0, 3);
        //list.slice(0, size)
        this.setState({ books: array, noResult: false });
      })
      .catch((error) => {
        console.log("search->error: ", error);
        this.setState({ noResult: true });
      });
  };

  render() {
    let books = null;
    if (this.state.books.length != 0) {
      books = this.state.books.map((book) => {
        <Card className={classes.Root} variant="outlined">
          <CardContent>
            <Typography
              className={classes.Title}
              color="textSecondary"
              gutterBottom
            >
              Word of the Day
            </Typography>
            {console.log(book.title)}
            <Typography variant="h5" component="h2">
              {book.title}
            </Typography>
            <Typography className={classes.Pos} color="textSecondary">
              adjective
            </Typography>
            <Typography variant="body2" component="p">
              well meaning and kindly.
              <br />
              {'"a benevolent smile"'}
            </Typography>
          </CardContent>
          <CardActions>
            <Button size="small">Learn More</Button>
          </CardActions>
        </Card>;
      });
    }

    return (
      <div>
        <br />
        <TextField
          id="outlined-basic"
          label="Insert title"
          variant="outlined"
          onChange={(event) => this.setSearchingTerm(event.target.value)}
          value={this.state.searchingTerm}
        >
          Book
        </TextField>
        <br />
        <button onClick={this.searchForBook}>Search</button>

        {this.state.books.length != 0 ? (
          { books }
        ) : (
          <p>No results for seraching term!</p>
        )}
        {/* {this.state.noResult ? <p>No results for seraching term!</p> : null} */}
      </div>
    );
  }
}

export default Search;
