import React, { Component } from "react";
import axios from "axios";
import {
  TextField,
  Paper,
  Card,
  CardContent,
  Typography,
  CardActions,
  Button,
  TablePagination,
} from "@material-ui/core";
import ReactPaginate from "react-paginate";
import classes from "./Search.css";
import Book from "./Book/Book";

class Search extends Component {
  state = {
    searchingTerm: "",
    noResult: false,
    books: [],
    start: true,
    message: "",
    pageNums: 0,
  };

  setSearchingTerm = (value) => {
    this.setState({ searchingTerm: value });
    console.log("term: ", this.state.searchingTerm);
  };

  searchForBook = () => {
    let term = this.state.searchingTerm;
    this.setState({ start: false, message: "wait..." });
    //str.replace("Microsoft", "W3Schools");
    term.replace(" ", "+");
    let url = "http://openlibrary.org/search.json?q=" + term + "/";
    axios
      .get(url)
      .then((response) => {
        console.log("search->response: ", response);
        if (response.data.docs.length == 0) {
          console.log("array empty");
          this.setState({ noResult: true, message: "NO RESULTS" });
        }
        console.log("response: ", response.data.docs);
        let array = response.data.docs.slice(0, 15);
        let pgs = array.length / 10;
        // if (array.length % 10 > 0) {
        //   pgs++;
        // }
        //list.slice(0, size)
        this.setState({ books: array, noResult: false, pageNums: pgs });
      })
      .catch((error) => {
        console.log("search->error: ", error);
        this.setState({ noResult: true });
      });
  };

  render() {
    let content = [];
    return (
      <div>
        <br />
        <TextField
          id="outlined-basic"
          label="Insert title"
          variant="outlined"
          onChange={(event) => this.setSearchingTerm(event.target.value)}
          value={this.state.searchingTerm}
        >
          Book
        </TextField>
        <br />
        <button onClick={this.searchForBook}>Search</button>

        {this.state.start ? (
          <p>Wellcome! Please enter book title you want to find.</p>
        ) : this.state.books.length != 0 ? (
          <ol>
            <p>Found books:</p>
            <Paper>
              {this.state.books.map((book) => {
                console.log("book: ", book.title);
                // <p>res:{book.title}</p>;
                return (
                  <Card key={book.key} className={classes.Root}>
                    <CardContent>
                      <Typography
                        className={classes.Title}
                        color="textSecondary"
                        gutterBottom
                      >
                        Word of the Day
                      </Typography>
                      <Typography variant="h5" component="h2">
                        {book.title}
                      </Typography>
                      <Typography className={classes.pos} color="textSecondary">
                        {book.type}
                      </Typography>
                      <Typography variant="body2" component="p">
                        {book.author_name[0]}
                        <br />
                        {'"a benevolent smile"'}
                      </Typography>
                    </CardContent>
                    <CardActions>
                      <Button size="small">Learn More</Button>
                    </CardActions>
                  </Card>
                );

                //content.push(b);
              })}
              {/* <ReactPaginate
                pageCount={this.state.pageNums}
                pageRangeDisplayed={5}
                marginPagesDisplayed={this.state.pageNums}
              /> */}
            </Paper>
          </ol>
        ) : (
          <p>{this.state.message}</p>
        )}
      </div>
    );
  }
}

export default Search;
