import React, { Component } from "react";
import axios from "axios";
import {
  TextField,
  Paper,
  Card,
  CardContent,
  Typography,
  CardActions,
  Button,
} from "@material-ui/core";
import classes from "./Search.css";

class Search extends Component {
  state = {
    searchingTerm: "",
    noResult: false,
    books: [],
    start: true,
  };

  setSearchingTerm = (value) => {
    this.setState({ searchingTerm: value });
    console.log("term: ", this.state.searchingTerm);
  };

  searchForBook = () => {
    let term = this.state.searchingTerm;
    this.setState({ start: false });
    //str.replace("Microsoft", "W3Schools");
    term.replace(" ", "+");
    let url = "http://openlibrary.org/search.json?q=" + term + "/";
    axios
      .get(url)
      .then((response) => {
        console.log("search->response: ", response);
        if (response.data.docs.length == 0) {
          console.log("array empty");
          this.setState({ noResult: true });
        }
        console.log("response: ", response.data.docs);
        let array = response.data.docs.slice(0, 10);
        //list.slice(0, size)
        this.setState({ books: array, noResult: false });
      })
      .catch((error) => {
        console.log("search->error: ", error);
        this.setState({ noResult: true });
      });
  };

  render() {
    return (
      <div>
        <br />
        <TextField
          id="outlined-basic"
          label="Insert title"
          variant="outlined"
          onChange={(event) => this.setSearchingTerm(event.target.value)}
          value={this.state.searchingTerm}
        >
          Book
        </TextField>
        <br />
        <button onClick={this.searchForBook}>Search</button>
        <Paper>
          {!this.state.start &&
          this.state.noResult &&
          this.state.books.length != 0 ? (
            this.state.books.map((book) => {
              console.log("title: ", book.title);
              <CardContent>
                <Card>{book.title}</Card>
              </CardContent>;
            })
          ) : (
            <p>No results for seraching term!</p>
          )}
        </Paper>
      </div>
    );
  }
}

export default Search;
