import React, { Component } from "react";
import axios from "axios";
import { TextField } from "@material-ui/core";

class Search extends Component {
  state = {
    searchingTerm: "",
    noResult: true,
    books: [],
  };

  setSearchingTerm = (value) => {
    this.setState({ searchingTerm: value });
  };

  searchForBook = () => {
    let term = this.state.searchingTerm;

    //str.replace("Microsoft", "W3Schools");
    term.replace(" ", "+");
    let url = "https://openlibrary.org/dev/docs/api/search.json?=" + term + "/";
    axios
      .get(url)
      .then((response) => {
        let array = response.docs;
        this.setState({ books: array, noResult: false });
      })
      .catch((error) => {
        console.log("search->error: ", error);
        this.setState({ noResult: true });
      });
  };

  render() {
    return (
      <div>
        <br />
        <TextField
          id="outlined-basic"
          label="Insert title"
          variant="outlined"
          onChange={(event) => this.setSearchingTerm(event.target.value)}
          value={this.state.searchingTerm}
        >
          Book
        </TextField>
        <br />
        <button onClick={this.searchForBook}>Search</button>
        {this.state.noResult ? <p>No results for seraching term!</p> : null}
        {this.state.books.length != 0
          ? this.state.books.map((book) => {
              console.log(book.title);
            })
          : null}
      </div>
    );
  }
}

export default Search;
