import React, { Component } from "react";
import axios from "react-axios";
import { TextField } from "@material-ui/core";

class Search extends Component {
  state = {
    searchingTerm: "",
    noResult: true,
    books: [],
  };

  setSearchingTerm = (value) => {
    this.setState({ searchingTerm: value });
  };

  searchForBook = () => {
    let term = this.state.searchingTerm;

    //str.replace("Microsoft", "W3Schools");
    term.replace(" ", "+");
    let url =
      " https://openlibrary.org/dev/docs/api/search.json?=" + term + "/";
    axios
      .get(url)
      .then((response) => {
        let array = response.docs;
        this.setState({ books: array, noResult: false });
      })
      .catch((error) => {
        this.setState({ noResult: true });
      });
  };

  render() {
    return (
      <div>
        <br />
        <TextField
          id="outlined-basic"
          label="Insert title"
          variant="outlined"
          onChange={(event) => this.setSearchingTerm(event.target.value)}
          value={this.state.searchingTerm}
        >
          Book
        </TextField>
        <br />
        <button onClick={this.searchForBook}>Search</button>
        {this.state.noResult ? (
          <p>There is no book for searching term! Try again...</p>
        ) : (
          this.state.map((book) => {
            console.log("Result: ", book.title);
          })
        )}
      </div>
    );
  }
}

export default Search;
